import { useState } from "react";
import { toast } from "react-toastify";
import { useAuth } from "../context/AuthContext";
import {
  PlusIcon,
  TrashIcon,
  PencilIcon,
  CheckIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";

const TodoList = ({ todos, setTodos, loading }) => {
  const [newTodo, setNewTodo] = useState({ title: "", description: "" });
  const [editingTodo, setEditingTodo] = useState(null);
  const [editText, setEditText] = useState({ title: "", description: "" });
  const { api } = useAuth();

  const handleAddTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const response = await api.post("/todos", newTodo);
      if (response.data.success) {
        setTodos([...todos, response.data.data]);
        setNewTodo({ title: "", description: "" });
        toast.success("Todo added successfully!");
      }
    } catch (error) {
      console.error("Error adding todo:", error);
      toast.error(error.response?.data?.message || "Failed to add todo");
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      const response = await api.delete(`/todos/${id}`);
      if (response.data.success) {
        setTodos(todos.filter((todo) => todo._id !== id));
        toast.success("Todo deleted successfully!");
      }
    } catch (error) {
      console.error("Error deleting todo:", error);
      toast.error(error.response?.data?.message || "Failed to delete todo");
    }
  };

  const handleToggleComplete = async (id) => {
    try {
      const todo = todos.find((t) => t._id === id);
      const response = await api.put(`/todos/${id}`, {
        completed: !todo.completed,
      });
      if (response.data.success) {
        setTodos(todos.map((t) => (t._id === id ? response.data.data : t)));
        toast.success(
          `Todo marked as ${!todo.completed ? "completed" : "incomplete"}!`
        );
      }
    } catch (error) {
      console.error("Error updating todo:", error);
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  const handleEditTodo = async (id) => {
    if (!editText.title.trim()) return;

    try {
      const response = await api.put(`/todos/${id}`, editText);
      if (response.data.success) {
        setTodos(todos.map((t) => (t._id === id ? response.data.data : t)));
        setEditingTodo(null);
        setEditText({ title: "", description: "" });
        toast.success("Todo updated successfully!");
      }
    } catch (error) {
      console.error("Error editing todo:", error);
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-white"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="bg-white/20 backdrop-blur-lg rounded-2xl shadow-2xl p-8 border border-white/30">
        <h1 className="text-3xl font-bold text-white mb-8 text-center">
          Todo List
        </h1>

        <form onSubmit={handleAddTodo} className="mb-8">
          <div className="flex flex-col gap-2">
            <input
              type="text"
              value={newTodo.title}
              onChange={(e) =>
                setNewTodo((prev) => ({ ...prev, title: e.target.value }))
              }
              placeholder="Todo title..."
              className="px-4 py-2 rounded-lg bg-white/20 text-white placeholder-white/70 border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400 focus:border-transparent transition duration-200"
            />
            <input
              type="text"
              value={newTodo.description}
              onChange={(e) =>
                setNewTodo((prev) => ({ ...prev, description: e.target.value }))
              }
              placeholder="Description (optional)..."
              className="px-4 py-2 rounded-lg bg-white/20 text-white placeholder-white/70 border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400 focus:border-transparent transition duration-200"
            />
            <button
              type="submit"
              className="px-4 py-2 bg-gradient-to-r from-rose-500 to-fuchsia-600 text-white rounded-lg hover:from-rose-600 hover:to-fuchsia-700 focus:outline-none focus:ring-2 focus:ring-rose-400 focus:ring-offset-2 transition duration-200 transform hover:scale-105 shadow-lg flex items-center justify-center gap-2"
            >
              <PlusIcon className="h-5 w-5" />
              Add Todo
            </button>
          </div>
        </form>

        {todos.length === 0 ? (
          <div className="text-center text-white/90">
            <p className="text-lg">No todos found. Add your first todo!</p>
          </div>
        ) : (
          <ul className="space-y-4">
            {todos.map((todo) => (
              <li
                key={todo._id}
                className="flex items-center justify-between p-4 bg-white/20 rounded-lg border border-white/30"
              >
                {editingTodo === todo._id ? (
                  <div className="flex-1 flex flex-col gap-2">
                    <input
                      type="text"
                      value={editText.title}
                      onChange={(e) =>
                        setEditText((prev) => ({
                          ...prev,
                          title: e.target.value,
                        }))
                      }
                      className="px-3 py-1 rounded bg-white/20 text-white border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400"
                    />
                    <input
                      type="text"
                      value={editText.description}
                      onChange={(e) =>
                        setEditText((prev) => ({
                          ...prev,
                          description: e.target.value,
                        }))
                      }
                      className="px-3 py-1 rounded bg-white/20 text-white border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400"
                    />
                    <div className="flex gap-2 mt-2">
                      <button
                        onClick={() => handleEditTodo(todo._id)}
                        className="p-1 text-green-500 hover:text-green-600"
                      >
                        <CheckIcon className="h-5 w-5" />
                      </button>
                      <button
                        onClick={() => {
                          setEditingTodo(null);
                          setEditText({ title: "", description: "" });
                        }}
                        className="p-1 text-red-500 hover:text-red-600"
                      >
                        <XMarkIcon className="h-5 w-5" />
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="flex items-center gap-3 flex-1">
                      <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() => handleToggleComplete(todo._id)}
                        className="h-5 w-5 rounded border-white/30 text-rose-500 focus:ring-rose-400"
                      />
                      <div
                        className={`flex-1 ${
                          todo.completed ? "line-through opacity-50" : ""
                        }`}
                      >
                        <h3 className="text-white font-medium">{todo.title}</h3>
                        {todo.description && (
                          <p className="text-white/70 text-sm mt-1">
                            {todo.description}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => {
                          setEditingTodo(todo._id);
                          setEditText({
                            title: todo.title,
                            description: todo.description || "",
                          });
                        }}
                        className="p-1 text-blue-500 hover:text-blue-600"
                      >
                        <PencilIcon className="h-5 w-5" />
                      </button>
                      <button
                        onClick={() => handleDeleteTodo(todo._id)}
                        className="p-1 text-red-500 hover:text-red-600"
                      >
                        <TrashIcon className="h-5 w-5" />
                      </button>
                    </div>
                  </>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default TodoList;
