import { useState } from "react";
import { toast } from "react-toastify";
import { useAuth } from "../context/AuthContext";
import {
  PlusIcon,
  TrashIcon,
  PencilIcon,
  CheckIcon,
  XMarkIcon,
  ClipboardDocumentListIcon,
} from "@heroicons/react/24/outline";

const TodoList = ({ todos, setTodos, loading }) => {
  const [newTodo, setNewTodo] = useState({ title: "", description: "" });
  const [editingTodo, setEditingTodo] = useState(null);
  const [editText, setEditText] = useState({ title: "", description: "" });
  const { api, user } = useAuth();

  const handleAddTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const response = await api.post("/todos", newTodo);
      if (response.data.success) {
        setTodos([...todos, response.data.data]);
        setNewTodo({ title: "", description: "" });
        toast.success("Todo added successfully!");
      }
    } catch (error) {
      console.error("Error adding todo:", error);
      toast.error(error.response?.data?.message || "Failed to add todo");
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      const response = await api.delete(`/todos/${id}`);
      if (response.data.success) {
        setTodos(todos.filter((todo) => todo._id !== id));
        toast.success("Todo deleted successfully!");
      }
    } catch (error) {
      console.error("Error deleting todo:", error);
      toast.error(error.response?.data?.message || "Failed to delete todo");
    }
  };

  const handleToggleComplete = async (id) => {
    try {
      const todo = todos.find((t) => t._id === id);
      const response = await api.put(`/todos/${id}`, {
        completed: !todo.completed,
      });
      if (response.data.success) {
        setTodos(todos.map((t) => (t._id === id ? response.data.data : t)));
        toast.success(
          `Todo marked as ${!todo.completed ? "completed" : "incomplete"}!`
        );
      }
    } catch (error) {
      console.error("Error updating todo:", error);
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  const handleEditTodo = async (id) => {
    if (!editText.title.trim()) return;

    try {
      const response = await api.put(`/todos/${id}`, editText);
      if (response.data.success) {
        setTodos(todos.map((t) => (t._id === id ? response.data.data : t)));
        setEditingTodo(null);
        setEditText({ title: "", description: "" });
        toast.success("Todo updated successfully!");
      }
    } catch (error) {
      console.error("Error editing todo:", error);
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  // Separate todos into active and completed
  const activeTodos = todos.filter((todo) => !todo.completed);
  const completedTodos = todos.filter((todo) => todo.completed);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-gray-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-2xl font-semibold text-gray-800">
              Welcome back, {user?.name}!
            </h1>
            <p className="text-gray-600 mt-1">
              Here's an overview of your tasks
            </p>
          </div>
          <div className="bg-blue-50 p-3 rounded-full">
            <ClipboardDocumentListIcon className="h-8 w-8 text-blue-500" />
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
            <div className="text-gray-600">Total Tasks</div>
            <div className="text-2xl font-semibold text-gray-800">
              {todos.length}
            </div>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
            <div className="text-gray-600">Active Tasks</div>
            <div className="text-2xl font-semibold text-gray-800">
              {activeTodos.length}
            </div>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
            <div className="text-gray-600">Completed</div>
            <div className="text-2xl font-semibold text-gray-800">
              {completedTodos.length}
            </div>
          </div>
        </div>
      </div>

      {/* Add Task Section */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Add New Task
        </h2>
        <form onSubmit={handleAddTodo}>
          <div className="flex flex-col gap-3">
            <input
              type="text"
              value={newTodo.title}
              onChange={(e) =>
                setNewTodo((prev) => ({ ...prev, title: e.target.value }))
              }
              placeholder="What needs to be done?"
              className="px-4 py-2 rounded-lg bg-gray-50 text-gray-800 placeholder-gray-400 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200"
            />
            <input
              type="text"
              value={newTodo.description}
              onChange={(e) =>
                setNewTodo((prev) => ({ ...prev, description: e.target.value }))
              }
              placeholder="Add details... (optional)"
              className="px-4 py-2 rounded-lg bg-gray-50 text-gray-800 placeholder-gray-400 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:border-transparent transition duration-200"
            />
            <button
              type="submit"
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2 transition duration-200 flex items-center justify-center gap-2"
            >
              <PlusIcon className="h-5 w-5" />
              Add Task
            </button>
          </div>
        </form>
      </div>

      {/* Tasks List Section */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          My Todo List
        </h2>

        {todos.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-500 text-lg">
              Your task list is empty. Time to be productive!
            </p>
          </div>
        ) : (
          <div className="space-y-6">
            {/* Active Tasks */}
            {activeTodos.length > 0 && (
              <div>
                <h3 className="text-lg font-medium text-gray-700 mb-3">
                  Active Tasks
                </h3>
                <div className="space-y-3">
                  {activeTodos.map((todo) => (
                    <div
                      key={todo._id}
                      className="p-4 rounded-lg border border-gray-200 hover:border-blue-200 transition-colors duration-200"
                    >
                      {editingTodo === todo._id ? (
                        <div className="flex flex-col gap-2">
                          <input
                            type="text"
                            value={editText.title}
                            onChange={(e) =>
                              setEditText((prev) => ({
                                ...prev,
                                title: e.target.value,
                              }))
                            }
                            className="px-3 py-2 rounded bg-white text-gray-800 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
                          />
                          <input
                            type="text"
                            value={editText.description}
                            onChange={(e) =>
                              setEditText((prev) => ({
                                ...prev,
                                description: e.target.value,
                              }))
                            }
                            className="px-3 py-2 rounded bg-white text-gray-800 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
                          />
                          <div className="flex gap-2 mt-2">
                            <button
                              onClick={() => handleEditTodo(todo._id)}
                              className="p-1 text-green-600 hover:text-green-700"
                            >
                              <CheckIcon className="h-5 w-5" />
                            </button>
                            <button
                              onClick={() => {
                                setEditingTodo(null);
                                setEditText({ title: "", description: "" });
                              }}
                              className="p-1 text-red-600 hover:text-red-700"
                            >
                              <XMarkIcon className="h-5 w-5" />
                            </button>
                          </div>
                        </div>
                      ) : (
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3 flex-1">
                            <input
                              type="checkbox"
                              checked={todo.completed}
                              onChange={() => handleToggleComplete(todo._id)}
                              className="h-5 w-5 rounded border-gray-300 text-blue-500 focus:ring-blue-400"
                            />
                            <div className="flex-1">
                              <h3 className="text-gray-800 font-medium">
                                {todo.title}
                              </h3>
                              {todo.description && (
                                <p className="text-sm mt-1 text-gray-600">
                                  {todo.description}
                                </p>
                              )}
                            </div>
                          </div>
                          <div className="flex gap-2 ml-4">
                            <button
                              onClick={() => {
                                setEditingTodo(todo._id);
                                setEditText({
                                  title: todo.title,
                                  description: todo.description || "",
                                });
                              }}
                              className="p-1 text-gray-500 hover:text-gray-700"
                            >
                              <PencilIcon className="h-5 w-5" />
                            </button>
                            <button
                              onClick={() => handleDeleteTodo(todo._id)}
                              className="p-1 text-gray-500 hover:text-gray-700"
                            >
                              <TrashIcon className="h-5 w-5" />
                            </button>
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Completed Tasks */}
            {completedTodos.length > 0 && (
              <div>
                <h3 className="text-lg font-medium text-gray-700 mb-3">
                  Completed Tasks
                </h3>
                <div className="space-y-3">
                  {completedTodos.map((todo) => (
                    <div
                      key={todo._id}
                      className="p-4 rounded-lg border border-gray-200 bg-gray-50"
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3 flex-1">
                          <input
                            type="checkbox"
                            checked={todo.completed}
                            onChange={() => handleToggleComplete(todo._id)}
                            className="h-5 w-5 rounded border-gray-300 text-blue-500 focus:ring-blue-400"
                          />
                          <div className="flex-1">
                            <h3 className="text-gray-500 font-medium line-through">
                              {todo.title}
                            </h3>
                            {todo.description && (
                              <p className="text-sm mt-1 text-gray-400 line-through">
                                {todo.description}
                              </p>
                            )}
                          </div>
                        </div>
                        <div className="flex gap-2 ml-4">
                          <button
                            onClick={() => handleDeleteTodo(todo._id)}
                            className="p-1 text-gray-400 hover:text-gray-600"
                          >
                            <TrashIcon className="h-5 w-5" />
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default TodoList;
