import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { AuthProvider, useAuth } from "./context/AuthContext";
import Login from "./pages/Login";
import Register from "./pages/Register";
import ForgotPassword from "./pages/ForgotPassword";
import ResetPassword from "./pages/ResetPassword";
import TodoList from "./pages/TodoList";
import Profile from "./pages/Profile";
import AdminPanel from "./pages/AdminPanel";
import ClientPanel from "./pages/ClientPanel";
import Navbar from "./components/Navbar";

// Role-based route protection component
const ProtectedRoute = ({ element, allowedRole, redirectPath }) => {
  const { isAuthenticated, user } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // If user's role matches the allowed role, show the component
  if (user.role === allowedRole) {
    return element;
  }

  // If user's role doesn't match, redirect to their appropriate panel
  return <Navigate to={redirectPath} replace />;
};

const AppContent = () => {
  const { isAuthenticated, api, loading: authLoading, user } = useAuth();
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTodos = async () => {
      if (!isAuthenticated || authLoading) {
        setLoading(false);
        setTodos([]);
        return;
      }

      try {
        const response = await api.get("/todos/my");
        if (response.data.success) {
          setTodos(response.data.data);
        } else {
          setTodos([]);
        }
      } catch (error) {
        console.error("Error fetching todos:", error);
        setTodos([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTodos();
  }, [isAuthenticated, api, authLoading]);

  if (authLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-gray-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Routes>
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <Navigate to={user.role === 0 ? "/admin" : "/client"} replace />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
        <Route
          path="/admin"
          element={
            <ProtectedRoute
              element={<AdminPanel />}
              allowedRole={0}
              redirectPath="/client"
            />
          }
        />
        <Route
          path="/client"
          element={
            <ProtectedRoute
              element={<ClientPanel />}
              allowedRole={1}
              redirectPath="/admin"
            />
          }
        />
        <Route
          path="/profile"
          element={
            isAuthenticated ? <Profile /> : <Navigate to="/login" replace />
          }
        />
        <Route
          path="/login"
          element={!isAuthenticated ? <Login /> : <Navigate to="/" replace />}
        />
        <Route
          path="/register"
          element={
            !isAuthenticated ? <Register /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/forgot-password"
          element={
            !isAuthenticated ? <ForgotPassword /> : <Navigate to="/" replace />
          }
        />
        <Route
          path="/reset-password/:token"
          element={
            !isAuthenticated ? <ResetPassword /> : <Navigate to="/" replace />
          }
        />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <AppContent />
      </AuthProvider>
    </Router>
  );
}

export default App;
