import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useLocation,
} from "react-router-dom";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { AuthProvider, useAuth } from "./context/AuthContext";
import Login from "./pages/Login";
import Register from "./pages/Register";
import ForgotPassword from "./pages/ForgotPassword";
import ResetPassword from "./pages/ResetPassword";
import Profile from "./pages/Profile";
import AdminPanel from "./pages/AdminPanel";
import ClientPanel from "./pages/ClientPanel";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import TodoList from "./pages/TodoList";
import UserList from "./pages/UserList";
import AdminTodoList from "./pages/AdminTodoList";
import NotFound from "./pages/NotFound";

// Role-based route protection component
const ProtectedRoute = ({ element, allowedRole, redirectPath }) => {
  const { isAuthenticated, user } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // If user's role matches the allowed role, show the component
  if (user.role === allowedRole) {
    return element;
  }

  // If user's role doesn't match, redirect to their appropriate panel
  return <Navigate to={redirectPath} replace />;
};

const AppContent = () => {
  const { user, isAuthenticated, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  const isAdmin = user?.role === 0;
  const isUser = user?.role === 1;

  // Public routes that don't require authentication
  const publicRoutes = [
    "/",
    "/login",
    "/register",
    "/forgot-password",
    "/reset-password",
  ];

  // Check if current route is public
  const isPublicRoute = publicRoutes.includes(location.pathname);

  // Redirect to login if trying to access protected route while not authenticated
  if (!isAuthenticated && !isPublicRoute) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/reset-password" element={<ResetPassword />} />

          {/* Protected Routes */}
          {isAuthenticated && (
            <>
              <Route path="/profile" element={<Profile />} />
              <Route path="/todos" element={<TodoList />} />
              {isAdmin && (
                <>
                  <Route path="/admin/users" element={<UserList />} />
                  <Route path="/admin/todos" element={<AdminTodoList />} />
                </>
              )}
              {isUser && (
                <>
                  <Route path="/user/todos" element={<TodoList />} />
                </>
              )}
            </>
          )}

          {/* Catch-all route */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <AuthProvider>
        <ToastContainer
          position="top-right"
          autoClose={3000}
          hideProgressBar={false}
          newestOnTop
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
        />
        <AppContent />
      </AuthProvider>
    </Router>
  );
};

export default App;
