import { useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import {
  PlusIcon,
  TrashIcon,
  PencilIcon,
  CheckIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";

const TodoList = ({ todos, setTodos, loading }) => {
  const [newTodo, setNewTodo] = useState("");
  const [editingTodo, setEditingTodo] = useState(null);
  const [editText, setEditText] = useState("");

  const handleAddTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) return;

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/todos",
        { text: newTodo },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTodos([...todos, response.data]);
      setNewTodo("");
      toast.success("Todo added successfully!");
    } catch (error) {
      toast.error(error.response?.data?.message || "Failed to add todo");
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/todos/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTodos(todos.filter((todo) => todo._id !== id));
      toast.success("Todo deleted successfully!");
    } catch (error) {
      toast.error(error.response?.data?.message || "Failed to delete todo");
    }
  };

  const handleToggleComplete = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const todo = todos.find((t) => t._id === id);
      const response = await axios.put(
        `http://localhost:5000/api/todos/${id}`,
        { completed: !todo.completed },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTodos(todos.map((t) => (t._id === id ? response.data : t)));
      toast.success(
        `Todo marked as ${!todo.completed ? "completed" : "incomplete"}!`
      );
    } catch (error) {
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  const handleEditTodo = async (id) => {
    if (!editText.trim()) return;

    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:5000/api/todos/${id}`,
        { text: editText },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTodos(todos.map((t) => (t._id === id ? response.data : t)));
      setEditingTodo(null);
      setEditText("");
      toast.success("Todo updated successfully!");
    } catch (error) {
      toast.error(error.response?.data?.message || "Failed to update todo");
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-white"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="bg-white/20 backdrop-blur-lg rounded-2xl shadow-2xl p-8 border border-white/30">
        <h1 className="text-3xl font-bold text-white mb-8 text-center">
          Todo List
        </h1>

        <form onSubmit={handleAddTodo} className="mb-8">
          <div className="flex gap-2">
            <input
              type="text"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
              placeholder="Add a new todo..."
              className="flex-1 px-4 py-2 rounded-lg bg-white/20 text-white placeholder-white/70 border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400 focus:border-transparent transition duration-200"
            />
            <button
              type="submit"
              className="px-4 py-2 bg-gradient-to-r from-rose-500 to-fuchsia-600 text-white rounded-lg hover:from-rose-600 hover:to-fuchsia-700 focus:outline-none focus:ring-2 focus:ring-rose-400 focus:ring-offset-2 transition duration-200 transform hover:scale-105 shadow-lg"
            >
              <PlusIcon className="h-5 w-5" />
            </button>
          </div>
        </form>

        {todos.length === 0 ? (
          <div className="text-center text-white/90">
            <p className="text-lg">No todos found. Add your first todo!</p>
          </div>
        ) : (
          <ul className="space-y-4">
            {todos.map((todo) => (
              <li
                key={todo._id}
                className="flex items-center justify-between p-4 bg-white/20 rounded-lg border border-white/30"
              >
                {editingTodo === todo._id ? (
                  <div className="flex-1 flex gap-2">
                    <input
                      type="text"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      className="flex-1 px-3 py-1 rounded bg-white/20 text-white border border-white/30 focus:outline-none focus:ring-2 focus:ring-rose-400"
                    />
                    <button
                      onClick={() => handleEditTodo(todo._id)}
                      className="p-1 text-green-500 hover:text-green-600"
                    >
                      <CheckIcon className="h-5 w-5" />
                    </button>
                    <button
                      onClick={() => {
                        setEditingTodo(null);
                        setEditText("");
                      }}
                      className="p-1 text-red-500 hover:text-red-600"
                    >
                      <XMarkIcon className="h-5 w-5" />
                    </button>
                  </div>
                ) : (
                  <>
                    <div className="flex items-center gap-3 flex-1">
                      <input
                        type="checkbox"
                        checked={todo.completed}
                        onChange={() => handleToggleComplete(todo._id)}
                        className="h-5 w-5 rounded border-white/30 text-rose-500 focus:ring-rose-400"
                      />
                      <span
                        className={`flex-1 text-white ${
                          todo.completed ? "line-through opacity-50" : ""
                        }`}
                      >
                        {todo.text}
                      </span>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => {
                          setEditingTodo(todo._id);
                          setEditText(todo.text);
                        }}
                        className="p-1 text-blue-500 hover:text-blue-600"
                      >
                        <PencilIcon className="h-5 w-5" />
                      </button>
                      <button
                        onClick={() => handleDeleteTodo(todo._id)}
                        className="p-1 text-red-500 hover:text-red-600"
                      >
                        <TrashIcon className="h-5 w-5" />
                      </button>
                    </div>
                  </>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default TodoList;
